package de.rub.nds.tlsscanner.clientscanner.constants;

import java.math.BigInteger;
import java.util.Comparator;
import java.util.List;
import javax.swing.SortOrder;

public enum PrimeModulus {
    BITSIZE_2(2, "3"),
    BITSIZE_4(4, "11"),
    BITSIZE_8(8, "241"),
    BITSIZE_16(16, "51061"),
    BITSIZE_32(32, "2532901439"),
    BITSIZE_64(64, "13772603011413202207"),
    BITSIZE_128(128, "272204737614830781438768392683701027529"),
    BITSIZE_256(
            256, "79262915755488246589817627239744612331543745677662430106790210816782234902623"),
    BITSIZE_512(
            512,
            "7945990110779639501190009032235692165888325199788474370362714626022604361613209684451167105397020646034833032599664410085095393451685716719782124610133707"),
    BITSIZE_1024(
            1024,
            ""),
    BITSIZE_2048(
            2048,
            ""),
    BITSIZE_3072(
            3072,
            ""),
    BITSIZE_4096(
            4096,
            ""),
    BITSIZE_5120(
            5120,
            ""),
    BITSIZE_6144(
            6144,
            ""),
    BITSIZE_7168(
            7168,
            ""),
    BITSIZE_8192(
            8192,
            "");

    private final int bitLength;
    private final String modulus;

    private PrimeModulus(int bitLength, String modulus) {
        this.bitLength = bitLength;
        this.modulus = modulus;
    }

    public int getBitLength() {
        return bitLength;
    }

    public BigInteger getModulus() {
        return new BigInteger(this.modulus);
    }

    public static void sort(List<PrimeModulus> moduli) {
        sort(moduli, SortOrder.ASCENDING);
    }

    public static void sort(List<PrimeModulus> moduli, SortOrder order) {
        Comparator<PrimeModulus> primeModulusComparator =
                new Comparator<>() {
                    @Override
                    public int compare(PrimeModulus modulus1, PrimeModulus modulus2) {
                        return modulus1.getBitLength() - modulus2.getBitLength();
                    }
                };

        if (order == SortOrder.DESCENDING) {
            primeModulusComparator = primeModulusComparator.reversed();
        }

        moduli.sort(primeModulusComparator);
    }
}
